//execute the case then check for the condition

package c_WhileTest;

public class C_DoWhileTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i=1;  
		 do{  
	       System.out.println(i);  
	       i++;  
		 }while(i<=10);  
		 
		 int j=1;  
		 do{  
	       System.out.println(j);  
	       j++;  
		 }while(j<=10);
	}
}
